  const { t, i18n } = useTranslation();

  return (
    <Router>
      <div className="app-wrapper">
        <header>
          <div className="header-container">
            <img src={logo} alt="–õ–æ–≥–æ—Ç–∏–ø –ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å" className="logo-left" loading="lazy" />
            <nav>
              <ul className="nav-menu centered">
                <li data-aos="fade-down"><Link to="/">{t("home", "–ì–æ–ª–æ–≤–Ω–∞")}</Link></li>
