import React, { useEffect, useState, Suspense } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import AOS from "aos";
import "aos/dist/aos.css";
import { useTranslation } from "react-i18next";
import "./css/style.css";
import logo from "./img/logo.png";
import backgroundImage from "./img/background.jpg";
import ReviewsPage from "./pages/ReviewsPage";
import ContactsPage from "./pages/ContactsPage";
import PortfolioPage from "./pages/PortfolioPage";
import PricingPage from "./pages/PricingPage";
import AdminPanel from "./pages/AdminPanel";
import { db } from "./firebase";
import { doc, getDoc, setDoc, increment } from "firebase/firestore";

function HomePage() {
  const { t } = useTranslation();
  const [views, setViews] = useState(0);

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const updateViews = async () => {
      const ref = doc(db, "views", "home");
      try {
        const snap = await getDoc(ref);
        if (snap.exists()) {
          await setDoc(ref, { count: increment(1) }, { merge: true });
        } else {
          await setDoc(ref, { count: 1 });
        }
        const updatedSnap = await getDoc(ref);
        setViews(updatedSnap.data().count);
      } catch (err) {
        console.error("Counter error:", err.message);
      }
    };

    updateViews();
  }, []);

  return (
    <main
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "center",
        color: "white",
        textAlign: "center",
        position: "relative",
        padding: "0",
        margin: "0",
        scrollBehavior: "smooth"
      }}
    >
      <Helmet>
        <title>ПромЕлектроСервіс | Електромонтажні роботи</title>
        <meta name="description" content="Монтаж сонячних електростанцій, електропроводка, освітлення. Професійні послуги електрика." />
        <meta property="og:title" content="ПромЕлектроСервіс" />
        <meta property="og:description" content="Професійні електромонтажні роботи будь-якої складності" />
        <meta property="og:image" content="/img/background.jpg" />
        <meta property="og:url" content="https://promelektroservice.vercel.app" />
        <meta name="google-site-verification" content="EKyNJQ7R7g2qP-jUOytPcpzJzUg2EAweD7ckfZE-o28" />
        <link rel="icon" href="/favicon.ico" />
      </Helmet>
      <div
        style={{
          backgroundColor: "rgba(0, 0, 0, 0)",
          padding: "20px",
          borderRadius: "10px",
          maxWidth: "800px",
          marginTop: "0"
        }}
        data-aos="fade-down"
      >
        <h1 className="hero-title fancy-title" style={{ marginTop: "0" }}>
          {t("companyName", "ПромЕлектроСервіс")}
        </h1>
        <h2 className="hero-subtitle">
          {t("companyDescription", "Професійні електромонтажні роботи будь-якої складності")}
        </h2>
        <div className="description">
          {[...Array(7).keys()].map(index => (
            <p key={index}>{t(`services.${index}`, `— Послуга ${index + 1}`)}</p>
          ))}
        </div>
      </div>

      <div style={{
        position: "fixed",
        bottom: "10px",
        left: "10px",
        fontSize: "14px",
        color: "#ccc"
      }}>
        👁 {t("views", "Переглядів")}: {views}
      </div>
    </main>
  );
}

function App() {
  const { t, i18n } = useTranslation();

  return (
    <Router>
      <div className="app-wrapper">
        <header>
          <div className="header-container">
            <img src={logo} alt="Логотип ПромЕлектроСервіс" className="logo-left" loading="lazy" />
            <nav>
              <ul className="nav-menu centered">
                <li data-aos="fade-down"><Link to="/">{t("home", "Головна")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="50"><Link to="/portfolio">{t("portfolio", "Портфоліо")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="100"><Link to="/reviews">{t("reviews", "Відгуки")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="150"><Link to="/pricing">{t("pricing", "Ціни")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="200"><Link to="/contacts">{t("contacts", "Контакти")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="250"><Link to="/admin">Admin</Link></li>
              </ul>
            </nav>
            <div className="language-switcher">
              <button onClick={() => i18n.changeLanguage("ua")}>UA</button>
              <button onClick={() => i18n.changeLanguage("ru")}>RU</button>
              <button onClick={() => i18n.changeLanguage("en")}>EN</button>
            </div>
          </div>
        </header>

        <Suspense fallback={<div>Loading...</div>}>
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/portfolio" element={<PortfolioPage />} />
            <Route path="/reviews" element={<ReviewsPage enableSearch={true} enableSort={true} />} />
            <Route path="/pricing" element={<PricingPage />} />
            <Route path="/contacts" element={<ContactsPage />} />
            <Route path="/admin" element={<AdminPanel />} />
          </Routes>
        </Suspense>

        <footer className="footer minimized-footer sticky-footer">
          <div className="footer-top">
            <a href="tel:+380666229776" className="footer-link">📞 +380666229776</a>
            <a href="mailto:gdvolya@gmail.com" className="footer-link">✉️ gdvolya@gmail.com</a>
          </div>
          <p>© 2025 ПромЕлектроСервіс. Всі права захищені.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
