function ReviewsPage() {
  const [reviews, setReviews] = useState(() => {
    const saved = localStorage.getItem("reviews");
    return saved ? JSON.parse(saved) : [];
  });
  const [inputValue, setInputValue] = useState("");
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    localStorage.setItem("reviews", JSON.stringify(reviews));
  }, [reviews]);

  const handleSubmit = () => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (inputValue.trim() && name.trim() && email.trim() && emailRegex.test(email)) {
      const newReview = { text: inputValue.trim(), name, email };
      setReviews([...reviews, newReview]);
      setInputValue("");
      setName("");
      setEmail("");
    } else {
      alert("–ë—É–¥—å –ª–∞—Å–∫–∞, –∑–∞–ø–æ–≤–Ω—ñ—Ç—å –≤—Å—ñ –ø–æ–ª—è –∫–æ—Ä–µ–∫—Ç–Ω–æ.");
    }
  };

  return (
    <main className="page-with-footer">
      <section id="reviews">
        <h2>–í—ñ–¥–≥—É–∫–∏ –∫–ª—ñ—î–Ω—Ç—ñ–≤</h2>
        <input
          type="text"
          placeholder="–í–∞—à–µ —ñ–º‚Äô—è"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
        />
        <input
          type="email"
          placeholder="–í–∞—à email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        />
        <textarea
          placeholder="–ù–∞–ø–∏—à—ñ—Ç—å —Å–≤—ñ–π –≤—ñ–¥–≥—É–∫..."
          className="review-textarea"
          value={inputValue}
          onChange={(e) => setInputValue(e.target.value)}
        ></textarea>
        <div style={{ textAlign: "center" }}>
          <button className="btn" onClick={handleSubmit}>–ù–∞–¥—ñ—Å–ª–∞—Ç–∏</button>
        </div>
        <div className="reviews-list">
          {reviews.map((review, index) => (
            <div key={index} className="review-item">
              <p><strong>{review.name}</strong> ({review.email})</p>
              <p>{review.text}</p>
            </div>
          ))}
        </div>
      </section>
    </main>
  );
}

