        <form className="contact-form" onSubmit={handleSubmit}>
          <input type="text" placeholder="–í–∞—à–µ —ñ–º‚Äô—è" required />
          <input type="email" placeholder="–í–∞—à email" required />
          <textarea placeholder="–í–∞—à–µ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è" required></textarea>
          <div style={{ textAlign: "center" }}>
            <button type="submit" className="btn">–ù–∞–¥—ñ—Å–ª–∞—Ç–∏</button>
