import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import "./css/style.css";
import logo from "./img/logo.png";
import project1 from "./img/project1.jpg";
import project2 from "./img/project2.jpg";
import project3 from "./img/project3.jpg";
import project4 from "./img/project4.jpg";
import project5 from "./img/project5.jpg";
import project6 from "./img/project6.jpg";
import project7 from "./img/project7.jpg";
import backgroundImage from "./img/background.jpg";
import ReviewsPage from "./pages/ReviewsPage";
import ContactsPage from "./pages/ContactsPage"; // ✅ импорт из файла
import { db } from "./firebase";

function HomePage() {
  return (
    <main
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        color: "white",
        textAlign: "center",
        position: "relative",
        padding: "20px"
      }}
    >
      <div style={{
        backgroundColor: "rgba(0, 0, 0, 0)",
        padding: "10px",
        borderRadius: "10px",
        maxWidth: "300px"
      }}>
        <h1 className="hero-title fancy-title">ПромЕлектроСервіс</h1>
        <p className="hero-subtitle">Професійні електромонтажні роботи будь-якої складності</p>
        <div className="description">
          <p>— Монтаж сонячних електростанцій (СЕС)</p>
          <p>— Прокладання кабельних ліній</p>
          <p>— Встановлення щитів, автоматів, розеток, освітлення</p>
          <p>— Введення об'єктів в експлуатацію</p>
          <p>— Ремонт і заміна старої електропроводки</p>
          <p>— Всі види зовнішніх та внутрішніх робіт</p>
          <p>— Обслуговування електроустановок</p>
          <p>— Електротехнічні вимірювання та протоколи</p>
        </div>
      </div>
    </main>
  );
}

function PortfolioPage() {
  return (
    <main className="page-with-footer">
      <section id="portfolio">
        <h2>Портфоліо</h2>
        <div className="portfolio-gallery">
          <img src={project1} alt="Проєкт 1" className="portfolio-img" />
          <img src={project2} alt="Проєкт 2" className="portfolio-img" />
          <img src={project3} alt="Проєкт 3" className="portfolio-img" />
          <img src={project4} alt="Проєкт 4" className="portfolio-img" />
          <img src={project5} alt="Проєкт 5" className="portfolio-img" />
          <img src={project6} alt="Проєкт 6" className="portfolio-img" />
          <img src={project7} alt="Проєкт 7" className="portfolio-img" />
        </div>
      </section>
    </main>
  );
}

function PricingPage() {
  return (
    <main className="page-with-footer">
      <section id="pricing">
        <h2>Ціни</h2>
        <ul className="price-list">
          <li>Монтаж СЕС — від 15000 грн</li>
          <li>Електропроводка — від 120 грн/м²</li>
          <li>Встановлення щитка — від 1000 грн</li>
          <li>Освітлення — від 200 грн/точка</li>
        </ul>
      </section>
    </main>
  );
}

function App() {
  return (
    <Router>
      <div className="app-wrapper">
        <header>
          <div className="header-container">
            <img src={logo} alt="Логотип" className="logo-left" />
            <nav>
              <ul className="nav-menu centered">
                <li><Link to="/">Головна</Link></li>
                <li><Link to="/portfolio">Портфоліо</Link></li>
                <li><Link to="/reviews">Відгуки</Link></li>
                <li><Link to="/pricing">Ціни</Link></li>
                <li><Link to="/contacts">Контакти</Link></li>
              </ul>
            </nav>
          </div>
        </header>

        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/portfolio" element={<PortfolioPage />} />
          <Route path="/reviews" element={<ReviewsPage />} />
          <Route path="/pricing" element={<PricingPage />} />
          <Route path="/contacts" element={<ContactsPage />} />
        </Routes>

        <footer className="footer minimized-footer sticky-footer">
          <div className="footer-top">
            <a href="tel:+380666229776" className="footer-link">📞 +380666229776</a>
            <a href="mailto:gdvolya@gmail.com" className="footer-link">✉️ gdvolya@gmail.com</a>
          </div>
          <p>© 2025 ПромЕлектроСервіс. Всі права захищені.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
