import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import AOS from "aos";
import "aos/dist/aos.css";
import "./css/style.css";
import logo from "./img/logo.png";
import project1 from "./img/project1.jpg";
import project2 from "./img/project2.jpg";
import project3 from "./img/project3.jpg";
import project4 from "./img/project4.jpg";
import project5 from "./img/project5.jpg";
import project6 from "./img/project6.jpg";
import project7 from "./img/project7.jpg";
import backgroundImage from "./img/background.jpg";
import ReviewsPage from "./pages/ReviewsPage";
import ContactsPage from "./pages/ContactsPage";
import { db } from "./firebase";
import { doc, getDoc, setDoc, increment } from "firebase/firestore";

function HomePage() {
  const [views, setViews] = useState(0);

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const updateViews = async () => {
      const ref = doc(db, "views", "home");
      try {
        const snap = await getDoc(ref);
        if (snap.exists()) {
          await setDoc(ref, { count: increment(1) }, { merge: true });
        } else {
          await setDoc(ref, { count: 1 });
        }
        const updatedSnap = await getDoc(ref);
        setViews(updatedSnap.data().count);
      } catch (err) {
        console.error("Counter error:", err.message);
      }
    };

    updateViews();
  }, []);

  return (
    <main
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "center",
        color: "white",
        textAlign: "center",
        position: "relative",
        padding: "0",
        margin: "0"
      }}
      itemScope
      itemType="https://schema.org/LocalBusiness"
    >
      <Helmet>
        <title>–ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å | –ï–ª–µ–∫—Ç—Ä–æ–º–æ–Ω—Ç–∞–∂–Ω—ñ —Ä–æ–±–æ—Ç–∏</title>
        <meta name="description" content="–ú–æ–Ω—Ç–∞–∂ —Å–æ–Ω—è—á–Ω–∏—Ö –µ–ª–µ–∫—Ç—Ä–æ—Å—Ç–∞–Ω—Ü—ñ–π, –µ–ª–µ–∫—Ç—Ä–æ–ø—Ä–æ–≤–æ–¥–∫–∞, –æ—Å–≤—ñ—Ç–ª–µ–Ω–Ω—è. –ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –ø–æ—Å–ª—É–≥–∏ –µ–ª–µ–∫—Ç—Ä–∏–∫–∞." />
        <meta property="og:title" content="–ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å" />
        <meta property="og:description" content="–ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –µ–ª–µ–∫—Ç—Ä–æ–º–æ–Ω—Ç–∞–∂–Ω—ñ —Ä–æ–±–æ—Ç–∏ –±—É–¥—å-—è–∫–æ—ó —Å–∫–ª–∞–¥–Ω–æ—Å—Ç—ñ" />
        <meta property="og:image" content="/img/background.jpg" />
        <meta property="og:url" content="https://promelektroservice.vercel.app" />
        <meta name="google-site-verification" content="EKyNJQ7R7g2qP-jUOytPcpzJzUg2EAweD7ckfZE-o28" />
        <link rel="icon" href="/favicon.ico" />
        <script type="application/ld+json">{`
          {
            "@context": "https://schema.org",
            "@type": "LocalBusiness",
            "name": "–ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å",
            "image": "https://promelektroservice.vercel.app/img/background.jpg",
            "url": "https://promelektroservice.vercel.app",
            "telephone": "+380666229776",
            "email": "gdvolya@gmail.com",
            "address": {
              "@type": "PostalAddress",
              "addressCountry": "UA"
            }
          }
        `}</script>
      </Helmet>

      <div
        style={{
          backgroundColor: "rgba(0, 0, 0, 0)",
          padding: "20px",
          borderRadius: "10px",
          maxWidth: "800px",
          marginTop: "0"
        }}
        data-aos="fade-down"
      >
        <h1 className="hero-title fancy-title" style={{ marginTop: "0" }}>
          –ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å
        </h1>
        <h2 className="hero-subtitle">
          –ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –µ–ª–µ–∫—Ç—Ä–æ–º–æ–Ω—Ç–∞–∂–Ω—ñ —Ä–æ–±–æ—Ç–∏ –±—É–¥—å-—è–∫–æ—ó —Å–∫–ª–∞–¥–Ω–æ—Å—Ç—ñ
        </h2>
        <div className="description">
          <p>‚Äî –ú–æ–Ω—Ç–∞–∂ —Å–æ–Ω—è—á–Ω–∏—Ö –µ–ª–µ–∫—Ç—Ä–æ—Å—Ç–∞–Ω—Ü—ñ–π (–°–ï–°)</p>
          <p>‚Äî –ü—Ä–æ–∫–ª–∞–¥–∞–Ω–Ω—è –∫–∞–±–µ–ª—å–Ω–∏—Ö –ª—ñ–Ω—ñ–π</p>
          <p>‚Äî –°–±–æ—Ä–∫–∞ —Ç–∞ –≤—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—è —â–∏—Ç—ñ–≤ –∞–≤—Ç–æ–º–∞—Ç–∏–∫–∏</p>
          <p>‚Äî –í–≤–µ–¥–µ–Ω–Ω—è –æ–±'—î–∫—Ç—ñ–≤ –≤ –µ–∫—Å–ø–ª—É–∞—Ç–∞—Ü—ñ—é</p>
          <p>‚Äî –†–µ–º–æ–Ω—Ç –µ–ª–µ–∫—Ç—Ä–æ–æ–±–ª–∞–¥–Ω–∞–Ω–Ω—è</p>
          <p>‚Äî –û–±—Å–ª—É–≥–æ–≤—É–≤–∞–Ω–Ω—è –µ–ª–µ–∫—Ç—Ä–æ—É—Å—Ç–∞–Ω–æ–≤–æ–∫</p>
          <p>‚Äî –ï–ª–µ–∫—Ç—Ä–æ—Ç–µ—Ö–Ω—ñ—á–Ω—ñ –≤–∏–º—ñ—Ä—é–≤–∞–Ω–Ω—è —Ç–∞ –ø—Ä–æ—Ç–æ–∫–æ–ª–∏</p>
        </div>
      </div>

      <div style={{
        position: "fixed",
        bottom: "10px",
        left: "10px",
        fontSize: "14px",
        color: "#ccc"
      }}>
        üëÅ –ü–µ—Ä–µ–≥–ª—è–¥—ñ–≤: {views}
      </div>
    </main>
  );
}

function PortfolioPage() {
  return (
    <main className="page-with-footer">
      <section id="portfolio">
        <h1>–ü–æ—Ä—Ç—Ñ–æ–ª—ñ–æ</h1>
        <h2>–ù–∞—à—ñ —Ä–æ–±–æ—Ç–∏</h2>
        <div className="portfolio-gallery">
          {[project1, project2, project3, project4, project5, project6, project7].map((img, i) => (
            <img key={i} src={img} alt={`–ü—Ä–æ—î–∫—Ç ${i + 1}`} className="portfolio-img" data-aos="zoom-in" />
          ))}
        </div>
      </section>
    </main>
  );
}

function PricingPage() {
  return (
    <main className="page-with-footer">
      <section id="pricing">
        <h1>–¶—ñ–Ω–∏</h1>
        <h2>–í–∞—Ä—Ç—ñ—Å—Ç—å –æ—Å–Ω–æ–≤–Ω–∏—Ö –ø–æ—Å–ª—É–≥</h2>
