import React, { useEffect, useState } from "react";
import { Helmet } from "react-helmet";
import AOS from "aos";
import "aos/dist/aos.css";
import "./css/style.css";
import backgroundImage from "./img/background.jpg";
import { db, messaging } from "./firebase";
import { doc, getDoc, setDoc, increment } from "firebase/firestore";
import { getToken, onMessage } from "firebase/messaging";

function HomePage() {
  const [views, setViews] = useState(0);
  const [isSubscribed, setIsSubscribed] = useState(false);

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const updateViews = async () => {
      const ref = doc(db, "views", "home");
      try {
        const snap = await getDoc(ref);
        if (snap.exists()) {
          await setDoc(ref, { count: increment(1) }, { merge: true });
        } else {
          await setDoc(ref, { count: 1 });
        }
        const updatedSnap = await getDoc(ref);
        setViews(updatedSnap.data().count);
      } catch (err) {
        console.error("Counter error:", err.message);
      }
    };

    updateViews();

    Notification.requestPermission().then(permission => {
      if (permission === "granted") {
        getToken(messaging, { vapidKey: "BE6mx-nmfkaM-RJmDBA2rJVNYkQRX9Qayj4-zgSz4AM-IJFssiPlAA0XUAaGmlznUUDIpvkksUzzJbgS0glRKj8" })
          .then(token => {
            console.log("Push token:", token);
          })
          .catch(err => console.error("FCM error", err));
      }
    });

    onMessage(messaging, payload => {
      alert(`üîî –ù–æ–≤–µ –ø–æ–≤—ñ–¥–æ–º–ª–µ–Ω–Ω—è: ${payload.notification?.title}`);
    });
  }, []);

  const subscribeToPush = () => {
    Notification.requestPermission().then(permission => {
      if (permission === "granted") {
        getToken(messaging, {
          vapidKey: "BE6mx-nmfkaM-RJmDBA2rJVNYkQRX9Qayj4-zgSz4AM-IJFssiPlAA0XUAaGmlznUUDIpvkksUzzJbgS0glRKj8"
        })
          .then(currentToken => {
            if (currentToken) {
              console.log("Push token:", currentToken);
              setIsSubscribed(true);
            }
          })
          .catch(err => console.error("Token error:", err));
      }
    });
  };

  return (
    <main
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "center",
        color: "white",
        textAlign: "center",
        position: "relative",
        padding: "0",
        margin: "0"
      }}
    >
      <Helmet>
        <title>–ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å | –ï–ª–µ–∫—Ç—Ä–æ–º–æ–Ω—Ç–∞–∂–Ω—ñ —Ä–æ–±–æ—Ç–∏</title>
        <meta name="description" content="–ú–æ–Ω—Ç–∞–∂ —Å–æ–Ω—è—á–Ω–∏—Ö –µ–ª–µ–∫—Ç—Ä–æ—Å—Ç–∞–Ω—Ü—ñ–π, –µ–ª–µ–∫—Ç—Ä–æ–ø—Ä–æ–≤–æ–¥–∫–∞, –æ—Å–≤—ñ—Ç–ª–µ–Ω–Ω—è. –ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –ø–æ—Å–ª—É–≥–∏ –µ–ª–µ–∫—Ç—Ä–∏–∫–∞." />
        <meta property="og:title" content="–ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å" />
        <meta property="og:description" content="–ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –µ–ª–µ–∫—Ç—Ä–æ–º–æ–Ω—Ç–∞–∂–Ω—ñ —Ä–æ–±–æ—Ç–∏ –±—É–¥—å-—è–∫–æ—ó —Å–∫–ª–∞–¥–Ω–æ—Å—Ç—ñ" />
        <meta property="og:image" content="/img/background.jpg" />
        <meta property="og:url" content="https://promelektroservice.vercel.app" />
        <meta name="google-site-verification" content="EKyNJQ7R7g2qP-jUOytPcpzJzUg2EAweD7ckfZE-o28" />
        <link rel="icon" href="/favicon.ico" />
      </Helmet>
      <div
        style={{
          backgroundColor: "rgba(0, 0, 0, 0)",
          padding: "20px",
          borderRadius: "10px",
          maxWidth: "800px",
          marginTop: "0"
        }}
        data-aos="fade-down"
      >
        <h1 className="hero-title fancy-title" style={{ marginTop: "0" }}>
          –ü—Ä–æ–º–ï–ª–µ–∫—Ç—Ä–æ–°–µ—Ä–≤—ñ—Å
        </h1>
        <h2 className="hero-subtitle">
          –ü—Ä–æ—Ñ–µ—Å—ñ–π–Ω—ñ –µ–ª–µ–∫—Ç—Ä–æ–º–æ–Ω—Ç–∞–∂–Ω—ñ —Ä–æ–±–æ—Ç–∏ –±—É–¥—å-—è–∫–æ—ó —Å–∫–ª–∞–¥–Ω–æ—Å—Ç—ñ
        </h2>
        <div className="description">
          <p>‚Äî –ú–æ–Ω—Ç–∞–∂ —Å–æ–Ω—è—á–Ω–∏—Ö –µ–ª–µ–∫—Ç—Ä–æ—Å—Ç–∞–Ω—Ü—ñ–π (–°–ï–°)</p>
          <p>‚Äî –ü—Ä–æ–∫–ª–∞–¥–∞–Ω–Ω—è –∫–∞–±–µ–ª—å–Ω–∏—Ö –ª—ñ–Ω—ñ–π</p>
          <p>‚Äî –°–±–æ—Ä–∫–∞ —Ç–∞ –≤—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–Ω—è —â–∏—Ç—ñ–≤ –∞–≤—Ç–æ–º–∞—Ç–∏–∫–∏</p>
          <p>‚Äî –í–≤–µ–¥–µ–Ω–Ω—è –æ–±'—î–∫—Ç—ñ–≤ –≤ –µ–∫—Å–ø–ª—É–∞—Ç–∞—Ü—ñ—é</p>
          <p>‚Äî –†–µ–º–æ–Ω—Ç –µ–ª–µ–∫—Ç—Ä–æ–æ–±–ª–∞–¥–Ω–∞–Ω–Ω—è</p>
          <p>‚Äî –û–±—Å–ª—É–≥–æ–≤—É–≤–∞–Ω–Ω—è –µ–ª–µ–∫—Ç—Ä–æ—É—Å—Ç–∞–Ω–æ–≤–æ–∫</p>
          <p>‚Äî –ï–ª–µ–∫—Ç—Ä–æ—Ç–µ—Ö–Ω—ñ—á–Ω—ñ –≤–∏–º—ñ—Ä—é–≤–∞–Ω–Ω—è —Ç–∞ –ø—Ä–æ—Ç–æ–∫–æ–ª–∏</p>
        </div>
        <button
          onClick={subscribeToPush}
          style={{
            marginTop: "20px",
            padding: "10px 20px",
            backgroundColor: "#003366",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer"
          }}
        >
          {isSubscribed ? "–í–∏ –ø—ñ–¥–ø–∏—Å–∞–Ω—ñ –Ω–∞ —Å–ø–æ–≤—ñ—â–µ–Ω–Ω—è" : "–ü—ñ–¥–ø–∏—Å–∞—Ç–∏—Å—è –Ω–∞ —Å–ø–æ–≤—ñ—â–µ–Ω–Ω—è"}
        </button>
      </div>

      <div style={{
        position: "fixed",
        bottom: "10px",
        left: "10px",
        fontSize: "14px",
        color: "#ccc"
      }}>
        üëÅ –ü–µ—Ä–µ–≥–ª—è–¥—ñ–≤: {views}
      </div>
    </main>
  );
}

export default HomePage;
