import React, { useEffect, useState } from "react";
import { Helmet } from "react-helmet";
import AOS from "aos";
import "aos/dist/aos.css";
import "./css/style.css";
import backgroundImage from "./img/background.jpg";
import { db, messaging } from "./firebase";
import { doc, getDoc, setDoc, increment } from "firebase/firestore";
import { getToken, onMessage } from "firebase/messaging";

function HomePage() {
  const [views, setViews] = useState(0);
  const [isSubscribed, setIsSubscribed] = useState(false);

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const updateViews = async () => {
      const ref = doc(db, "views", "home");
      try {
        const snap = await getDoc(ref);
        if (snap.exists()) {
          await setDoc(ref, { count: increment(1) }, { merge: true });
        } else {
          await setDoc(ref, { count: 1 });
        }
        const updatedSnap = await getDoc(ref);
        setViews(updatedSnap.data().count);
      } catch (err) {
        console.error("Counter error:", err.message);
      }
    };

    updateViews();

    Notification.requestPermission().then(permission => {
      if (permission === "granted") {
        getToken(messaging, { vapidKey: "BE6mx-nmfkaM-RJmDBA2rJVNYkQRX9Qayj4-zgSz4AM-IJFssiPlAA0XUAaGmlznUUDIpvkksUzzJbgS0glRKj8" })
          .then(token => {
            console.log("Push token:", token);
          })
          .catch(err => console.error("FCM error", err));
      }
    });

    onMessage(messaging, payload => {
      alert(`🔔 Нове повідомлення: ${payload.notification?.title}`);
    });
  }, []);

  const subscribeToPush = () => {
    Notification.requestPermission().then(permission => {
      if (permission === "granted") {
        getToken(messaging, {
          vapidKey: "BE6mx-nmfkaM-RJmDBA2rJVNYkQRX9Qayj4-zgSz4AM-IJFssiPlAA0XUAaGmlznUUDIpvkksUzzJbgS0glRKj8"
        })
          .then(currentToken => {
            if (currentToken) {
              console.log("Push token:", currentToken);
              setIsSubscribed(true);
            }
          })
          .catch(err => console.error("Token error:", err));
      }
    });
  };

  return (
    <main
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        justifyContent: "flex-start",
        alignItems: "center",
        color: "white",
        textAlign: "center",
        position: "relative",
        padding: "0",
        margin: "0"
      }}
    >
      <Helmet>
        <title>ПромЕлектроСервіс | Електромонтажні роботи</title>
        <meta name="description" content="Монтаж сонячних електростанцій, електропроводка, освітлення. Професійні послуги електрика." />
        <meta property="og:title" content="ПромЕлектроСервіс" />
        <meta property="og:description" content="Професійні електромонтажні роботи будь-якої складності" />
        <meta property="og:image" content="/img/background.jpg" />
        <meta property="og:url" content="https://promelektroservice.vercel.app" />
        <meta name="google-site-verification" content="EKyNJQ7R7g2qP-jUOytPcpzJzUg2EAweD7ckfZE-o28" />
        <link rel="icon" href="/favicon.ico" />
      </Helmet>
      <div
        style={{
          backgroundColor: "rgba(0, 0, 0, 0)",
          padding: "20px",
          borderRadius: "10px",
          maxWidth: "800px",
          marginTop: "0"
        }}
        data-aos="fade-down"
      >
        <h1 className="hero-title fancy-title" style={{ marginTop: "0" }}>
          ПромЕлектроСервіс
        </h1>
        <h2 className="hero-subtitle">
          Професійні електромонтажні роботи будь-якої складності
        </h2>
        <div className="description">
          <p>— Монтаж сонячних електростанцій (СЕС)</p>
          <p>— Прокладання кабельних ліній</p>
          <p>— Сборка та встановлення щитів автоматики</p>
          <p>— Введення об'єктів в експлуатацію</p>
          <p>— Ремонт електрообладнання</p>
          <p>— Обслуговування електроустановок</p>
          <p>— Електротехнічні вимірювання та протоколи</p>
        </div>
        <button
          onClick={subscribeToPush}
          style={{
            marginTop: "20px",
            padding: "10px 20px",
            backgroundColor: "#003366",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer"
          }}
        >
          {isSubscribed ? "Ви підписані на сповіщення" : "Підписатися на сповіщення"}
        </button>
      </div>

      <div style={{
        position: "fixed",
        bottom: "10px",
        left: "10px",
        fontSize: "14px",
        color: "#ccc"
      }}>
        👁 Переглядів: {views}
      </div>
    </main>
  );
}

export default HomePage;
