import i18n from "i18next";
import { initReactI18next } from "react-i18next";

const resources = {
  ua: {
    translation: {
      companyName: "ПромЕлектроСервіс",
      companyDescription: "Професійні електромонтажні роботи будь-якої складності",
      services: [
        "— Монтаж сонячних електростанцій (СЕС)",
        "— Прокладання кабельних ліній",
        "— Сборка та встановлення щитів автоматики",
        "— Введення об'єктів в експлуатацію",
        "— Ремонт електрообладнання",
        "— Обслуговування електроустановок",
        "— Електротехнічні вимірювання та протоколи"
      ],
      views: "Переглядів",
      meta: {
        title: "ПромЕлектроСервіс | Електромонтажні роботи",
        description: "Монтаж сонячних електростанцій, електропроводка, освітлення. Професійні послуги електрика.",
        ogTitle: "ПромЕлектроСервіс",
        ogDescription: "Професійні електромонтажні роботи будь-якої складності"
      }
    }
  }
};

i18n.use(initReactI18next).init({
  resources,
  lng: "ua",
  fallbackLng: "ua",
  interpolation: {
    escapeValue: false
  }
});

export default i18n;
