import React, { useEffect, useState } from "react";
import { db } from "../firebase";
import { collection, getDocs, deleteDoc, doc } from "firebase/firestore";

function AdminPanel() {
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [contacts, setContacts] = useState([]);

  const correctPassword = "admin123";

  useEffect(() => {
    if (authenticated) {
      loadData();
    }
  }, [authenticated]);

  const loadData = async () => {
    const reviewsSnap = await getDocs(collection(db, "reviews"));
    setReviews(reviewsSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })));

    const contactsSnap = await getDocs(collection(db, "contacts"));
    setContacts(contactsSnap.docs.map(doc => ({ id: doc.id, ...doc.data() })));
  };

  const handleLogin = () => {
    if (password === correctPassword) {
      setAuthenticated(true);
    } else {
      alert("Невірний пароль");
    }
  };

  const handleDelete = async (type, id) => {
    await deleteDoc(doc(db, type, id));
    loadData();
  };

  if (!authenticated) {
    return (
      <div style={{ padding: 20 }}>
        <h2>Вхід в адмін-панель</h2>
        <input
          type="password"
          placeholder="Введіть пароль"
          value={password}
          onChange={e => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Увійти</button>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>Відгуки</h2>
      <table border="1">
        <thead>
          <tr><th>Ім'я</th><th>Email</th><th>Відгук</th><th>Дія</th></tr>
        </thead>
        <tbody>
          {reviews.map(r => (
            <tr key={r.id}>
              <td>{r.name}</td>
              <td>{r.email}</td>
              <td>{r.text}</td>
              <td><button onClick={() => handleDelete("reviews", r.id)}>Видалити</button></td>
            </tr>
          ))}
        </tbody>
      </table>

      <h2>Заявки з контактної форми</h2>
      <table border="1">
        <thead>
          <tr><th>Ім'я</th><th>Email</th><th>Телефон</th><th>Повідомлення</th><th>Дія</th></tr>
        </thead>
        <tbody>
          {contacts.map(c => (
            <tr key={c.id}>
              <td>{c.name}</td>
              <td>{c.email}</td>
              <td>{c.phone}</td>
              <td>{c.message}</td>
              <td><button onClick={() => handleDelete("contacts", c.id)}>Видалити</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default AdminPanel;
