import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import AOS from "aos";
import "aos/dist/aos.css";
import { useTranslation } from "react-i18next";
import "./css/style.css";
import logo from "./img/logo.png";
import project1 from "./img/project1.jpg";
import project2 from "./img/project2.jpg";
import project3 from "./img/project3.jpg";
import project4 from "./img/project4.jpg";
import project5 from "./img/project5.jpg";
import project6 from "./img/project6.jpg";
import project7 from "./img/project7.jpg";
import backgroundImage from "./img/background.jpg";
import ReviewsPage from "./pages/ReviewsPage";
import ContactsPage from "./pages/ContactsPage";
import { db } from "./firebase";
import { doc, getDoc, setDoc, increment } from "firebase/firestore";

function App() {
  const { t, i18n } = useTranslation();
  const [views, setViews] = useState(0);

  useEffect(() => {
    AOS.init({ duration: 1000, once: true });

    const updateViews = async () => {
      const ref = doc(db, "views", "home");
      try {
        const snap = await getDoc(ref);
        if (snap.exists()) {
          await setDoc(ref, { count: increment(1) }, { merge: true });
        } else {
          await setDoc(ref, { count: 1 });
        }
        const updatedSnap = await getDoc(ref);
        setViews(updatedSnap.data().count);
      } catch (err) {
        console.error("Counter error:", err.message);
      }
    };

    updateViews();
  }, []);

  return (
    <Router>
      <div className="app-wrapper">
        <header>
          <div className="header-container">
            <img src={logo} alt="Логотип" className="logo-left" />
            <nav>
              <ul className="nav-menu centered">
                <li data-aos="fade-down"><Link to="/">{t("home")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="50"><Link to="/portfolio">{t("portfolio")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="100"><Link to="/reviews">{t("reviews")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="150"><Link to="/pricing">{t("pricing")}</Link></li>
                <li data-aos="fade-down" data-aos-delay="200"><Link to="/contacts">{t("contacts")}</Link></li>
              </ul>
            </nav>
            <div className="language-switcher">
              <button onClick={() => i18n.changeLanguage("ua")}>UA</button>
              <button onClick={() => i18n.changeLanguage("ru")}>RU</button>
              <button onClick={() => i18n.changeLanguage("en")}>EN</button>
            </div>
          </div>
        </header>

        <main
          style={{
            backgroundImage: `url(${backgroundImage})`,
            backgroundSize: "cover",
            backgroundPosition: "center",
            minHeight: "100vh",
            display: "flex",
            flexDirection: "column",
            justifyContent: "flex-start",
            alignItems: "center",
            color: "white",
            textAlign: "center",
            position: "relative",
            padding: "0",
            margin: "0",
            scrollBehavior: "smooth"
          }}
          itemScope
          itemType="https://schema.org/LocalBusiness"
        >
          <Helmet>
            <title>{t("meta.title")}</title>
            <meta name="description" content={t("meta.description" )} />
            <meta property="og:title" content={t("meta.ogTitle" )} />
            <meta property="og:description" content={t("meta.ogDescription" )} />
            <meta property="og:image" content="/img/background.jpg" />
            <meta property="og:url" content="https://promelektroservice.vercel.app" />
            <meta name="google-site-verification" content="EKyNJQ7R7g2qP-jUOytPcpzJzUg2EAweD7ckfZE-o28" />
            <link rel="icon" href="/favicon.ico" />
            <script type="application/ld+json">{`
              {
                "@context": "https://schema.org",
                "@type": "LocalBusiness",
                "name": "ПромЕлектроСервіс",
                "image": "https://promelektroservice.vercel.app/img/background.jpg",
                "url": "https://promelektroservice.vercel.app",
                "telephone": "+380666229776",
                "email": "gdvolya@gmail.com",
                "address": {
                  "@type": "PostalAddress",
                  "addressCountry": "UA"
                }
              }
            `}</script>
          </Helmet>

          <div
            style={{
              backgroundColor: "rgba(0, 0, 0, 0)",
              padding: "20px",
              borderRadius: "10px",
              maxWidth: "800px",
              marginTop: "0"
            }}
            data-aos="fade-down"
          >
            <h1 className="hero-title fancy-title" style={{ marginTop: "0" }} itemProp="name">
              {t("companyName")}
            </h1>
            <h2 className="hero-subtitle" itemProp="description">
              {t("companyDescription")}
            </h2>
            <div className="description">
              <p>{t("services.0")}</p>
              <p>{t("services.1")}</p>
              <p>{t("services.2")}</p>
              <p>{t("services.3")}</p>
              <p>{t("services.4")}</p>
              <p>{t("services.5")}</p>
              <p>{t("services.6")}</p>
            </div>
          </div>

          <div style={{
            position: "fixed",
            bottom: "10px",
            left: "10px",
            fontSize: "14px",
            color: "#ccc"
          }}>
            👁 {t("views")}: {views}
          </div>
        </main>

        <footer className="footer minimized-footer sticky-footer">
          <div className="footer-top">
            <a href="tel:+380666229776" className="footer-link">📞 +380666229776</a>
            <a href="mailto:gdvolya@gmail.com" className="footer-link">✉️ gdvolya@gmail.com</a>
          </div>
          <p>© 2025 ПромЕлектроСервіс. Всі права захищені.</p>
        </footer>
      </div>
    </Router>
  );
}

export default App;
